#include "Player.hpp"#define ACCELERATION 0.5Player::~Player() {}Player::Player() {	_screenSize = Director::getInstance()->getWinSize();	_floatingTimerMax = 2;	_floatingTimer = 0;	_speed = PLAYER_INITIAL_SPEED;	_maxSpeed = PLAYER_INITIAL_SPEED;	_floating = false;	_nextPosition = Vec2(0, 0);	_nextPosition.y = _screenSize.height * 0.6f;	_state = kPlayerMoving;	_jumping = false;	_hasFloated = false;}Player * Player::create() {	Player * player = new Player();	if (player && player->initWithFile("blank.png")) {		player->autorelease();		player->setSize();		player->initPlayer();		return player;	}	CC_SAFE_DELETE(player);	return NULL;}void Player::update(float dt) {}void Player::reset() {	_speed = PLAYER_INITIAL_SPEED;	_maxSpeed = PLAYER_INITIAL_SPEED;	_vector = Vec2(0, 0);	this->setFloating(false);	this->setRotation(0);	_nextPosition.y = _screenSize.height * 0.6f;	this->setPosition(Vec2(_screenSize.width * 0.2f, _nextPosition.y));	_state = kPlayerMoving;	_jumping = false;	_hasFloated = false;}void Player::setFloating(bool value) {}void Player::initPlayer() {}